stackoptions.questionvariables:
aa: rand(7)+3;
bb: rand_with_prohib(1,4,[aa]);

exp1: aa*x^2;      /* function 1 */
exp2: bb*x^3;     /* function 2 */

l1: rand(2)+1               /* limits of function 1, lower limit*/
u1: l1+1+rand(3);        /* upper limit*/
ta1:  int(exp1, x, l1, u1);

l2:0;                             /* limits of function 2, lower limit*/
u2: rand(3)+2;             /* upper limit*/    
ta2: int(exp2, x, l2, u2); 


/*jsxgraph settings */ 
BB1: [-l1, aa*(u1+1)^2, u1+2, -(1/6)*aa*(u1+1)^2]     /* boundingbox:[left, top, right, bottom]*/ 
a: ev(exp1, x=l1);    /* y axis ticks*/
b: ev(exp1, x=u1);

BB2: [-u2, bb*(u2+0.2)^3, u2+2, -(1/6)*bb*(u2+0.2)^3]     /* boundingbox:[left, top, right, bottom]*/ 
c: ev(exp2, x=l2);    /* y axis ticks*/
d: ev(exp2, x=u2);


/*Maxima plots - are not used in this question, we used JSX Graphs instead*/
plot1: plot([ exp1,  line(l1,0,l1, aa*u1^2), line(u1,0,u1, aa*u1^2)], [x,-(u1+1), u1+1],[y,-2, aa*u1^2],[box, false],[yx_ratio, 1],[axes, solid]);

plot2: plot(exp2, [x, l2-1,u2+1], [y, bb*(l2)^3-1, bb*(u2)^3+1], [box, false], [axes,solid]);
