stackoptions.questionvariables:
ordergreat(x, y);
/* Randomisation (same as 18.3.2.4 T) */
/* First find zeroes of partial derivatives */
x1: rand_with_step(1, 4, 1);
x2: -x1;
y1: rand_with_prohib(-4, 4, [0]);
/* Then construct partial derivatives */
f_x: expand(3 * (x - x1) * (x - x2));
f_y: expand (2 * (y - y1));
/* Lastly, construct f */
f: int(f_x, x) + int(f_y, y) + rand_with_prohib(-5, 5, [0]);

f_xx: diff(f_x, x);
f_yy: diff(f_y, y);
f_xy: diff(f_x, y);
D: f_xx * f_yy - f_xy^2;

ta1: f_xx;
ta2: f_yy;
ta3: f_xy;
ta11: ev(f_xx, x=x1, y=y1);
ta12: ev(f_xx, x=x2, y=y1);
ta21: ev(f_yy, x=x1, y=y1);
ta22: ev(f_yy, x=x2, y=y1);
ta31: ev(f_xy, x=x1, y=y1);
ta32: ev(f_xy, x=x2, y=y1);

D1: ev(D, x=x1, y=y1);
ta41: if D1 < 0 
  then [["< 0", true], ["= 0", false], ["> 0", false]]
  else (
    if D1 = 0
      then [["< 0", false], ["= 0", true], ["> 0", false]]
      else [["< 0", false], ["= 0", false], ["> 0", true]]
  );

D2: ev(D, x=x2, y=y1);
ta42: if D2 < 0 
  then [["< 0", true], ["= 0", false], ["> 0", false]]
  else (
    if D2 = 0
      then [["< 0", false], ["= 0", true], ["> 0", false]]
      else [["< 0", false], ["= 0", false], ["> 0", true]]
  );

ta51: if D1 < 0
  then [["local minimum", false], ["local maximum", false], ["saddle point", true], ["inconclusive", false]]
  else (
    if D1 = 0
      then [["local minimum", false], ["local maximum", false], ["saddle point", false], ["inconclusive", true]]
      else (
        if ta11 > 0
          then [["local minimum", true], ["local maximum", false], ["saddle point", false], ["inconclusive", false]]
          else [["local minimum", false], ["local maximum", true], ["saddle point", false], ["inconclusive", false]]
      )
  );

ta52: if D2 < 0
  then [["local minimum", false], ["local maximum", false], ["saddle point", true], ["inconclusive", false]]
  else (
    if D2 = 0
      then [["local minimum", false], ["local maximum", false], ["saddle point", false], ["inconclusive", true]]
      else (
        if ta12 > 0
          then [["local minimum", true], ["local maximum", false], ["saddle point", false], ["inconclusive", false]]
          else [["local minimum", false], ["local maximum", true], ["saddle point", false], ["inconclusive", false]]
      )
  );

/* Needed for worked solution */
D1sign: if D1 < 0 then "negative" else "positive";
D2sign: if D2 < 0 then "negative" else "positive";
