stackoptions.questionvariables:
ordergreat(x, y);

/*============*
/* Equation 1 */
/*============*/
/* Coefficient randomisation */
a11: rand_with_prohib(-4, 4, [0]);
a12: rand_with_prohib(-4, 4, [0, -a11, a11]);
b1: -a11 - a12;
c1: a11 * a12;
f1: rand_with_step(2, 4, 1) * c1;

/* Select variables */
deq1: 'diff(py, px, 2) + b1 * 'diff(py, px) + c1 * py = f1;
v1: rand([[y, x], [x, t]]);
deq1: ev(deq1, py=v1[1], px=v1[2]);

/* Steps for solution */
auxeq1: k^2 + b1 * k + c1 = 0;
cf1: ev(A * %e^(a11 * px) + B * %e^(a12 * px), px=v1[2]);
alpha1: f1 / c1;
pi1: alpha1;

ta1: cf1 + pi1;
ta11: ev(%e^(a11 * px), px=v1[2]);
ta12: ev(%e^(a12 * px), px=v1[2]);
ta1pi: pi1;


/*============*/
/* Equation 2 */
/*============*/
/* Coefficient randomisation */
a21: rand_with_prohib(-5, 5, [0]);
a22: rand_with_prohib(-5, 5, [0, -a21, a21]);
b2: -a21 - a22;
c2: a21 * a22;
l2: rand_with_step(2, 8, 1);

/* Select variables */
f2: l2 * px;
deq2: 'diff(py, px, 2) + b2 * 'diff(py, px) + c2 * py = f2;
v2: rand([[y, x], [x, t]]);
deq2: ev(deq2, py=v2[1], px=v2[2]);

/* Steps for solution */
auxeq2: k^2 + b2 * k + c2 = 0;
cf2: ev(A * %e^(a21 * px) + B * %e^(a22 * px), px=v2[2]);
pitest2: a * v2[2] + b;
dpitest2: diff(pitest2, v2[2]);
ddpitest2: diff(dpitest2, v2[2]);
pieq2: ddpitest2 + b2 * dpitest2 + c2 * pitest2 = ev(f2, px=v2[2]);
aa2: l2 / c2;
bb2: -b2 * l2 / c2;
pi2: aa2 * v2[2] + bb2;

ta2: cf2 + pi2;
ta21: ev(%e^(a21 * px), px=v2[2]);
ta22: ev(%e^(a22 * px), px=v2[2]);
ta2pi: pi2;


/*============*/
/* Equation 3 */
/*============*/
/* Coefficient randomisation */
re3: rand_with_prohib(-2, 2, [0]);
im3: sqrt(rand([2,3]));
a31: re3 + %i * im3;
a32: conjugate(a31);
b3: -a31 - a32;
c3: re3^2 + im3^2;
l3: rand_with_step(2, 8, 1);
k3: rand_with_step(2, 3, 1);
s3: rand(2);

/* Select variables */
f3: l3 * if s3 = 0 then sin(k3 * px) else cos(k3 * px);
deq3: 'diff(py, px, 2) + b3 * 'diff(py, px) + c3 * py = f3;
v3: rand([[y, x], [x, t]]);
deq3: ev(deq3, py=v3[1], px=v3[2]);

/* Steps for solution */
auxeq3: k^2 + b3 * k + c3 = 0;
cf3: ev(%e^(re3 * px) * (A * cos(im3 * px) + B * sin(im3 * px)), px=v3[2]);
pitest3: ev(a * cos(k3 * px) + b * sin(k3 * px), px=v3[2]);
dpitest3: diff(pitest3, v3[2]);
ddpitest3: diff(dpitest3, v3[2]);
pieq3: ddpitest3 + b3 * dpitest3 + c3 * pitest3 = ev(f3, px=v3[2]);
pieq3: collectterms(expand(pieq3));
pieq31:  (c3 - k3^2) * a + b3 * k3 * b = if s3 = 0 then 0 else l3;
pieq32: -b3 * k3 * a + (c3 - k3^2) * b = if s3 = 0 then l3 else 0;
pieq3sol: linsolve([pieq31, pieq32], [a, b]);
aa3: rhs(pieq3sol[1]);
bb3: rhs(pieq3sol[2]);
pi3: ev(aa3 * cos(k3 * px) + bb3 * sin(k3 * px), px=v3[2]);

ta3: cf3 + pi3;
ta31: ev(cos(im3 * px), px=v3[2]);
ta32: ev(sin(im3 * px), px=v3[2]);
ta33: ev(%e^(re3 * px), px=v3[2]);
ta3pi: pi3;


/*============*/
/* Equation 4 */
/*============*/
/* Coefficient randomisation */
im4: rand_with_step(2, 5, 1);
a41: %i * im4;
a42: -a41;
c4: im4^2;
l4: rand_with_step(2, 5, 1);
k4: rand_with_step(2, 8, 1);

/* Select variables */
f4: l4 * %e^(k4 * px);
deq4: 'diff(py, px, 2) + c4 * py = f4;
v4: rand([[y, x], [x, t]]);
deq4: ev(deq4, py=v4[1], px=v4[2]);

/* Steps for solution */
auxeq4: k^2 + c4 = 0;
cf4: ev(A * cos(im4 * px) + B * sin(im4 * px), px=v4[2]);
pitest4: ev(a * %e^(k4 * px), px=v4[2]);
dpitest4: diff(pitest4, v4[2]);
ddpitest4: diff(dpitest4, v4[2]);
pieq4: ddpitest4 + c4 * pitest4 = ev(f4, px=v4[2]);
pieq4: collectterms(expand(pieq4));
pieq41: k4^2 * a + c4 * a = l4;
pieq4sol: solve(pieq4, a);
aa4: rhs(pieq4sol[1]);
pi4: ev(pitest4, a=aa4);

ta4: cf4 + pi4;
ta41: ev(cos(im4 * px), px=v4[2]);
ta42: ev(sin(im4 * px), px=v4[2]);
ta4pi: pi4;

/*============*/
/* Equation 5 */
/*============*/
/* Coefficient randomisation */
re5: rand_with_step(2, 5, 1);
a51: re5;
a52: -a51;
c5: a51 * a52;
l5: rand_with_step(2, 5, 1);
k5: rand_with_prohib(2, 8, [re5]);

/* Select variables */
f5: l5 * %e^(k5 * px);
deq5: 'diff(py, px, 2) + c5 * py = f5;
v5: rand([[y, x], [x, t]]);
deq5: ev(deq5, py=v5[1], px=v5[2]);

/* Steps for solution */
auxeq5: k^2 + c5 = 0;
cf5: ev(A * %e^(re5 * px) + B * %e^(-re5 * px), px=v5[2]);
pitest5: ev(a * %e^(k5 * px), px=v5[2]);
dpitest5: diff(pitest5, v5[2]);
ddpitest5: diff(dpitest5, v5[2]);
pieq5: ddpitest5 + c5 * pitest5 = ev(f5, px=v5[2]);
pieq5: collectterms(expand(pieq5));
pieq51: k5^2 * a + c5 * a = l5;
pieq5sol: solve(pieq5, a);
aa5: rhs(pieq5sol[1]);
pi5: ev(pitest5, a=aa5);

ta5: cf5 + pi5;
ta51: ev(%e^(re5 * px), px=v4[2]);
ta52: ev(%e^(-re5 * px), px=v4[2]);
ta5pi: pi5;
