stackoptions.questionvariables:
makeratio(a,b,c):=(r1:integrate((x-a)*(x-b)^2,x,a,b),r2:integrate((x-a)*(x-b)^2,x,b,c),[r1/r2,[a,b,c],r1,r2]);
/* finding a,b,c which give nice ratios:
allposs:create_list (makeratio(a,b,c), a, [-4,-3,-2,-1], b, a+2, 3, c, b+1,b+3);
niceones:sublist(allposs,lambda([ex],is(ex[1]=16/11 or ex[1]=9/16)));
niceabc:maplist(second,niceones);
*/
niceabc:[[-4,-2,-1],[-4,-1,1],[-4,0,2],[-4,2,5],[-3,-1,0],[-3,0,2],[-3,1,3],[-3,3,6],[-2,0,1],[-2,1,3],[-2,2,4],[-1,1,2],[-1,2,4],[-1,3,5]];
[a,b,c]:rand(niceabc);
poly:(x-a)*(x-b)^2;
lin:2*x+rand_range(-2*a,10);
areaRatio:makeratio(a,b,c)[1];
Atrue:num(areaRatio);
Btrue:denom(areaRatio);
A:rand_range(Atrue-2,Atrue+2);
B:rand_range(Btrue-2,Btrue+2);
linIntegral:integrate(lin,x,a,c);
ta1:A+B+linIntegral

/*jsxgraph settings */
BBx:[a-1,c+1];
BBy:[ev(poly,[x=b])+1,ev(poly,[x=c])+1,-1];
BBy:append(BBy,[-lmax(BBy)/6]);
BB:[lmin(BBx),lmax(BBy),lmax(BBx),lmin(BBy)];
