questionvariables.text:
/* HSN AT002 */
tx:rand_range(-2,3);
ty:rand_range(-3,4);
cx:rand([1,-1]);
cy:1;
/* The following are based on applying translations tx, ty and scalings cx, cy to a known "nice" question */
T(x,y):=[cx*(x+tx),cy*(y+ty)];
TF(y):=expand(ev(T(0,y)[2],x=x/cx-tx)); /* given an expression y=[fn], return a transformed y=[fn2] in new coords */
quad:TF((x+1)^2-5);  /*ev(expand(cy*(ty- ((x)/(cx)-tx)^(2))),simp);*/
A:T(-2,-4);
B:T(3,11);
quad_TP:T(-1,-5);
quad_y0:ev(quad,x=0);
line:TF(3*x+2);
ax:lmin([A[1],B[1]]);
bx:lmax([A[1],B[1]]);
linem:coeff(line,x,1);
linec:coeff(line,x,0);
ta1:quad;
altta1:coeff(quad,x,2)*(x-quad_TP[1])^2+quad_TP[2];
ta2:linem;
int1:int(line-quad,x);
ta3:int(line-quad,x,ax,bx);


/*bounding box*/
bbTL:T(-7,15);
bbBR:T(6,-8);
BBx:[bbTL[1],bbBR[1],-1.5,1.5];
BBy:[bbTL[2],bbBR[2],-1.5,1.5];
BB:[lmin(BBx),lmax(BBy),lmax(BBx),lmin(BBy)];
