questionvariables.text:
exp5: e^x;
l1: 0;
u1: 2;
ta5: int(exp5, x, 0, 2); 

/* polynomial function*/
aa: rand(3)+1;
bb: aa+1+rand(2);
exp6: x*(x-aa)*(x-bb);
l2:0;
u2:aa;
ta6: int(exp6, x, l2, aa)+ abs(int(exp6, x, aa, bb));

/*jsxgraph settings */
[a,b]: [l1,u1];            /* matrix of limits for jsx graph*/
BB1: [-1,e^(u1+0.2),u1+1, -(1/6)*e^(u1+0.2)]     /* boundingbox:[left, top, right, bottom]*/


/*Maxima Plots*/
plot1: plot(exp5, [x,-0.3,2.3], [y, %e^(0)-2, %e^(2)+2], [box, false], [axes,solid]);

f1: diff(exp6,x);
f2: solve(f1);
coeff1: decimalplaces(rhs(f2[1]),3);
coeff2: decimalplaces(rhs(f2[2]),3);
plot2: plot(exp6, [x,l2-1,bb+1], [y,-10,10], [box, false], [axes,solid]);
plot3: plot(exp6, [x,l2-1,bb+1], [y, coeff2*(coeff2-aa)*(coeff2-bb), coeff1*(coeff1-aa)*(coeff1-bb)], [box, false], [axes,solid]);
