questionvariables.text:
aa: rand([2, 4, 6, 8]); 
bb: rand([3, 5, 7, 9]); 
cc: rand(4)+1;

f1: x + cc;
g1: sin(x);
exp1: f1 * g1;

f2: x^2 - bb * x + 1;
g2: e^x;
exp2: f2 * g2;

l1: 1;
u1: 2;
ta1: decimalplaces(float(int(exp1, x, l1, u1)),4); 

l2: 0;
u2: 1;
ta2: decimalplaces(float(int(exp2, x, l2, u2)),4);

/* Needed for solution */
df1: diff(f1, x);
ig1: int(g1, x);
fig1: f1 * ig1;
idfig1: int(df1 * ig1, x);
u11: subst(l1, x, fig1);
u12: subst(u1, x, fig1);
v11: subst(l1, x, idfig1);
v12: subst(u1, x, idfig1);

df2: diff(f2, x);
ig2: int(g2, x);
fig2: f2 * ig2;
idfig2: int(df2 * ig2, x);
u21: subst(l2, x, fig2);
u22: subst(u2, x, fig2);
v21: subst(l2, x, idfig2);
v22: subst(u2, x, idfig2);
