questionvariables.text:
ic : 1+rand(2);
mc : ic + 1 + rand(3);
oc : mc + 1 + rand(3);
ip : 10;
mp : 5;
op : 3; 
/*areas*/
ai : (ic^2)*pi;
am : (mc^2 - ic^2)*pi;
ao : (oc^2 - mc^2)*pi;
at : oc^2*pi;
/*probabilities*/
ei : (ic/oc)^2/2;
em : ((mc/oc)^2-(ic/oc)^2)/2;
eo : ((oc/oc)^2-(mc/oc)^2)/2;
ta1 : ip*ei + mp*em + op*eo;
