questionvariables.text:
variant: 1+rand(3); /* this one random variable controls all of the randomisation */

/*
"variant" determines the sizes of the random matrices
variant = 1 => A = (2x3), B = (2x2), C = (3x2)
variant = 2 => A = (3x2), B = (2x1), C = (1x3)
variant = 3 => A = (1x3), B = (3x2), C = (2x2)
the following block just implements this
*/
matrixlist: block ( [],
if variant = 1 then return([ matrix([rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1)], [rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1)]),  matrix([rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1)], [rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1)]),  matrix([rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1)],  [rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1)],  [rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1)])]), 
if variant = 2 then return([ matrix([rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1)], [rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1)], [rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1)]),  matrix([rand_with_step(-5, 5, 1)], [rand_with_step(-5, 5, 1)]),  matrix([rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1)])]), 
if variant = 3 then return([ matrix([rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1)]),  matrix([rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1)], [rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1)], [rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1)]),  matrix([rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1)], [rand_with_step(-5, 5, 1), rand_with_step(-5, 5, 1)]) ])
)

/* now we define the matrices themselves */
matrixa: matrixlist[1];
matrixb: matrixlist[2];
matrixc: matrixlist[3];

/* we don't need the matrices to do the question, just the matrix sizes, so give them different names */
sizea: matrix_size(matrixa);
sizeb: matrix_size(matrixb);
sizec: matrix_size(matrixc);

/*
the following variable is just defined because I need to have an object to name the "Model answer" in the case that the product is not defined
note that badlist is never used in any of the potential response trees!
*/
badlist: [ EMPTYANSWER , EMPTYANSWER ];

/*
the following three functions do all of the work for us
this way, if you want, you can change the sizes of the matrices in the big block above and it won't affect whether or not the question works

first function tests whether a product of exactly two matrices is defined
but again, this doesn't depend on the matrices, just on their sizes
so the inputs are not matrices, but lists [m, n] outputted by matrix_size
*/

isShortProductDefined(sizeAA,sizeBB) := block( [],
if sizeAA[2] = sizeBB[1] then true
else false
);

/*
second function tests whether the consecutive product of a list of matrices is defined 
the entries of the input list should be outputs of matrix_size
*/

isProductDefined(LL) := block( [ DD: true ],
for i:1 while i<length(LL) do ( DD: DD and isShortProductDefined(LL[i],LL[i+1]) ),
DD
);

/*
third function returns the size of the resultant product
returns badlist if product is undefined
*/

sizeOfProduct(LL) := block( [sizeAA: first(LL), sizeBB: last(LL)],
if isProductDefined(LL) = true then [ sizeAA[1], sizeBB[2] ]
else badlist
);

/* now the defined functions do all of the work to compute the teacher's answers */

ta11: isProductDefined( [ sizea , sizeb ] );
ta21: isProductDefined( [ sizeb , sizea ] );
ta31: isProductDefined( [ sizeb , sizec ] );
ta41: isProductDefined( [ sizec , sizeb ] );
ta51: isProductDefined( [ sizec , sizea ] );
ta61: isProductDefined( [ sizea , sizec ] );
ta71: isProductDefined( [ sizea , sizeb , sizec ] );
ta81: isProductDefined( [ sizea , sizec , sizeb ] );

ta12: sizeOfProduct( [ sizea , sizeb ] );
ta22: sizeOfProduct( [ sizeb , sizea ] );
ta32: sizeOfProduct( [ sizeb , sizec ] );
ta42: sizeOfProduct( [ sizec , sizeb ] );
ta52: sizeOfProduct( [ sizec , sizea ] );
ta62: sizeOfProduct( [ sizea , sizec ] );
ta72: sizeOfProduct( [ sizea , sizeb , sizec ] );
ta82: sizeOfProduct( [ sizea , sizec , sizeb ] );

/* this final function is just for automating the feedback */
feedbackFunction(XX) := block ( [],
if XX = badlist then return("\\text{product not defined}")
else return(XX)
)
